import React, { Component } from 'react'
import PropTypes from 'prop-types';
import './Event.css';

 class Event extends Component {
     state={
         showEventInfo:true
     };
     onDeleteClick=()=>{
         this.props.deleteClickHandler();
     }
    
    render() {
        const {name,description,location}=this.props.event;
        const {showEventInfo}=this.state;
        return (
            <div className="card card-body mb-3">
                <h4>
                    {name}{' '}
                <i 
                onClick={() =>
this.setState({showEventInfo:!this.state.showEventInfo})
                }
                
                     className="fas fa-sort-down" style={{cursor:'pointer'}}
                     />
                     <i
                     className="fas fa-times"
                     style={{cursor:'pointer',float:
                     'right',color:'red' }}  
                     onClick={this.onDeleteClick}
                     />   
                </h4>
                {showEventInfo ? (
                <ul className="list-group">
                    <li className="list-group-item">Description: {description}</li>
                    <li className="list-group-item">Location: {location}</li>
                </ul>
                ) : null }
            </div>
        )
    }
}
Event.propTypes = {
   event:PropTypes.object.isRequired,
   deleteClickHandler:PropTypes.func.isRequired
};
export default Event;
