import {GET_EVENTS,DELETE_EVENT,ADD_EVENT} from './types';
import axios from 'axios';

export const getEvents =() =>async dispatch=>{
  const res=await axios.get
  ();
  dispatch({
    type:GET_EVENTS,
    payload:res.data
  });
      
    };


export const deleteEvent =(id) =>{
    return{
        type:DELETE_EVENT,
        payload:id
    };
};

export const addEvent =event =>{
    return{
        type:ADD_EVENT,
        payload:event
    };
};
