import {GET_EVENTS,DELETE_EVENT,ADD_EVENT} from '../actions/types';


const initialState={
    events:[
        {
            id:1,
            name:'EventA',
            description:'descA',
            location:'Paris'
        },
        {
            id:2,
            name:'EventB',
            description:'descB',
            location:'Germany'
        },
        {
            id:3,
            name:'EventC',
            description:'descC',
            location:'Patna'
        },
        ]
};

export default function(state=initialState,action)
{
    switch(action.type){
case GET_EVENTS:
return {
    ...state
};
case DELETE_EVENT:
return {
    ...state,
    events:state.events.filter(
        event=>event.id!==action.payload
    )
};
case ADD_EVENT:
    return{
...state,events:[action.payload,...state.events]
    };
        default:
            return state;
    }
}
