import React, { Component } from 'react';
import {Consumer} from '../../context';
import TextInputGroup from '../layout/TextInputGroup';
//import {v1 as uuid} from 'uuid';
import axios from 'axios';

 class EditEvent extends Component {
state={
    name:'',
    description:'',
    location:'',
    errors:{}
};
async componentDidMount(){
    const {id}=this.props.match.params;
    const res=await axios.get(`https://my-json-server.typicode.com/priya-12345/EventData/events/${id}`);

    const event=res.data;
    this.setState({
        name:event.name,    
        description:event.description,
        location:event.location
    });
}
onSubmit=async (dispatch,e)=>{
    e.preventDefault();
    const{name,description,location}=this.state;
//check for errors

if(name===''){
    this.setState({errors: {name:'Name is required'}});
    return;
}

if(description===''){
    this.setState({errors: {description:'Description is required'}});
    return;
}

if(location===''){
    this.setState({errors: {location:'Location is required'}});
    return;
}

const updEvent={
name,
description,
location

}
   const {id}=this.props.match.params;
  const res=await axios.put(`https://my-json-server.typicode.com/priya-12345/EventData/events/${id}`,updEvent);
  dispatch({type:'UPDATE_EVENT',payload:res.data});
    this.setState({
        name:'',
        description:'',
        location:'',
        errors:{}
    });
    this.props.history.push('/');
};
onChange=(e)=>this.setState({[e.target.name]:e.target.value});

    render() {
        const{ name,description,location,errors}=this.state;
        return(
            <Consumer>
                {value=>{
const{dispatch}
=value;
return(
<div className="card mb-3">
                <div className="card-header">Edit Event</div>
                <div className="card-body">
<form onSubmit={this.onSubmit.bind(this,dispatch)}>
<TextInputGroup
label="Name"
name="name"
placeholder="Enter Name"
value={name}
onChange={this.onChange}
errors={errors.name}
/>

<TextInputGroup
label="Description"
name="description"
placeholder="Enter Description"
value={description}
onChange={this.onChange}
errors={errors.description}
/>

<TextInputGroup
label="Location"
name="location"
placeholder="Enter Location"
value={location}
onChange={this.onChange}
errors={errors.location}
/>
<input type="submit" value="Update Event" className="btn btn-light btn-block"/>
</form>
                    </div>
            </div>

)

                }}
            </Consumer>
        )
        
    }
}

export default EditEvent;
