import React, { Component } from 'react';
import Event from './Event';


class Events extends Component {
constructor()
{
super();
this.state={
events:[
    {
id:1,
name:'Slate & Crystal Events',
description:'abc',
location:'Switzerland'

},
{
    id:2,
    name :'Indigo Event Planning',
    description:'xyz',
    location:'kalyan'
    
    },
    {
        id:3,
        name:'Buttercup Events',
        description:'pqr',
        location:'Paris'
        
        }
]
};
}
deleteEvent=id=>
{
const {events} =this.state;
const newEvents=events.filter(event =>event.id !==id);
this.setState({
    events:newEvents
});
};
    render() {
        const {events} =this.state;
        return (
            <React.Fragment>
                {events.map(event =>(
                    <Event 
                    key={event.id} event={event}
                     deleteClickHandler={this.deleteEvent.bind(this,event.id)}
                   />
                ))}
            </React.Fragment>
        );
    }
}

export default Events;
