import React, { Component } from 'react';
import {Consumer} from '../../context';
import TextInputGroup from '../layout/TextInputGroup';
import {v1 as uuid} from 'uuid';


 class AddEvent extends Component {
state={
    name:'',
    description:'',
    location:'',
    errors:{}
};
onSubmit=(dispatch,e)=>{
    e.preventDefault();
    const{name,description,location}=this.state;
//check for errors

if(name===''){
    this.setState({errors: {name:'Name is required'}});
    return;
}

if(description===''){
    this.setState({errors: {description:'Description is required'}});
    return;
}

if(location===''){
    this.setState({errors: {location:'Location is required'}});
    return;
}


    const newEvent={
        id:uuid(),
        name,description,location
    }
    dispatch({type:'ADD_EVENT',payload:newEvent});
    this.setState({
        name:'',
        description:'',
        location:'',
        errors:{}
    });
};
onChange=(e)=>this.setState({[e.target.name]:e.target.value});

    render() {
        const{ name,description,location,errors}=this.state;
        return(
            <Consumer>
                {value=>{
const{dispatch}
=value;
return(
<div className="card mb-3">
                <div className="card-header">Add Event</div>
                <div className="card-body">
<form onSubmit={this.onSubmit.bind(this,dispatch)}>
<TextInputGroup
label="Name"
name="name"
placeholder="Enter Name"
value={name}
onChange={this.onChange}
errors={errors.name}
/>

<TextInputGroup
label="Description"
name="description"
placeholder="Enter Description"
value={description}
onChange={this.onChange}
errors={errors.description}
/>

<TextInputGroup
label="Location"
name="location"
placeholder="Enter Location"
value={location}
onChange={this.onChange}
errors={errors.location}
/>
<input type="submit" value="Add Event" className="btn btn-light btn-block"/>
</form>
                    </div>
            </div>

)

                }}
            </Consumer>
        )
        
    }
}

export default AddEvent;
